import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/App.css";

const Register = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user");
  const [error, setError] = useState(""); // New state for error
  const [loading, setLoading] = useState(false); // New state for loading
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(""); // Clear previous errors

    try {
      console.log("Sending registration request...");
      const response = await axios.post("http://localhost:5000/api/auth/register", 
        { email, password, role }
      );
      
      console.log("Registration successful:", response.data);
      navigate("/login");
      
    } catch (error) {
      // Detailed error logging
      console.error("Registration error:", error);
      
      // Check for different types of errors
      if (error.response) {
        // The request was made and the server responded with an error status (4xx, 5xx)
        setError(error.response.data.message || `Server error: ${error.response.status}`);
      } else if (error.request) {
        // The request was made but no response was received (backend down, network issue)
        setError("Cannot connect to server. Is the backend running on port 5000?");
      } else {
        // Something else happened
        setError("An unexpected error occurred: " + error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h2>Register</h2>
      
      {/* Display error message if exists */}
      {error && <div className="error-message" style={{color: 'red', marginBottom: '10px'}}>{error}</div>}
      
      <form onSubmit={handleRegister}>
        <input 
          type="email" 
          placeholder="Email" 
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        
        <button type="submit" disabled={loading}>
          {loading ? "Creating Account..." : "Register"}
        </button>
      </form>
    </div>
  );
};

export default Register;
